package org.icefaces.checkbox;import javax.faces.bean.SessionScoped;import javax.faces.bean.ManagedBean;@ManagedBean (name="checkboxBean")@SessionScopedpublic class CheckboxBean {    private boolean checked = false;	private String label = "checkboxButton";    public CheckboxBean() { }    public boolean isChecked() {        return checked;    }    public void setChecked(boolean checked) {        this.checked = checked;    }	    public String getLabel() {        return label;    }    public void setLabel(String label) {        this.label = label;    }}